import React from 'react'
import {Link , useParams} from 'react-router-dom'
import './detail.css'
import { useState ,useEffect } from 'react'
import axios from 'axios'


function Detail() {
	const [user, setUser] = useState({});

	let link = window.location.href
	let userId = link.replace('http://localhost:3000/#/list/view/','').toString()
    console.log("userId:"+userId)
	useEffect(
		function () {
			async function getCrudById() {
				try {
					const response = await axios.get(`https://dose.herokuapp.com/api/${userId}`
					);
					setUser(response.data[0]);
				} catch (error) {
					console.log("error", error);
				}
			}
			getCrudById();
		}
	);
	console.log(user)
  return (
	<div className="det">
		<h1>รายชื่อ</h1>
	 <h2>Name :<p>{user.name}</p></h2>
	 <h2>height :<p>{user.height}</p></h2>
	 <h2>weight :<p>{user.weight}</p></h2>
	 <h2>age :<p>{user.age}</p></h2>
	 <h2>DesCription :<p>{user.age}</p></h2>

	 <Link className='close' to='/list'>Close</Link>
	 </div>
	)
  }
export default Detail
