import React from 'react'
import './edir.css'
import {Link} from 'react-router-dom'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';
import { useEffect , useState} from 'react';

function Edit() {
    const noti = ()=>{toast.success('Edit success!', {
        position: "bottom-right",
        theme:'dark',
        autoClose: false,
        hideProgressBar: true,
        closeOnClick: false,
        pauseOnHover: true,
        draggable: true,
        progress: 1,
        });
    }
    const [user, setUser] = useState({});
    const [name,setName] = useState('')
	const [age,setAge]= useState(0)
	const [height,setHeight]=useState(0)
	const [weight,setWeight]=useState(0)
	const [desc,setDesc]=useState('')

	let link = window.location.href
	let userId = link.replace('http://localhost:3000/#/list/edit/','').toString()
    console.log("userId:"+userId)
	useEffect(
		function () {
			async function getCrudById() {
				try {
					const response = await axios.get(`https://dose.herokuapp.com/api/${userId}`
					);
					setUser(response.data[0]);
				} catch (error) {
					console.log("error", error);
				}
			}
			getCrudById();
		}
	);
  return (
    <div className="edit">
      <h1>Edit Data!!</h1>
        <div className="myfrom">
            <input type="text" placeholder='New name'  defaultValue={user.name} onChange={handleChange}/>
            <input type="number" placeholder='New age' defaultValue={user.age}  onChange={handleChange}/>
            <input type="number" placeholder='New height' defaultValue={user.height} onChange={handleChange}/>
            <input type="number" placeholder='New weight' defaultValue={user.weight} onChange={handleChange}/>
            <textarea  rows="5" placeholder='New Description' value={user.descrip} onChange={handleChange}></textarea>
            <div className="btng">
                <button className='editt' onClick={noti}>Edit</button>
                <Link to='/list' className='etli'>Close</Link>
                
            </div>
        </div>
        <ToastContainer
				position="bottom-right"
				autoClose={false}
				newestOnTop={false}
				closeOnClick={false}
				rtl={false}
				pauseOnFocusLoss
				draggable
		/>
    </div>
  )
}

export default Edit
